    public void ProduceTheProperOutput(string inputCsv, string expected)
    {
        var input = string.IsNullOrEmpty(inputCsv)
            ? Array.Empty<int>()
            : inputCsv.Split(',').Select(int.Parse);

        var tokenizer = new Tokenizer.Model();
        var actual = tokenizer.Decode(input);

        Assert.Equal(expected, actual);
    }
}